bool IsHot(string s, IDictionary<char, bool> dictionary)
{
    foreach (char chr in s)
    {
        if (dictionary.ContainsKey(chr))
        {
            return false;
        }

        dictionary.Add(chr, true);
    }

    return true;
}

void LessonA()
{
    IDictionary<char, bool> knownChar = new Dictionary<char, bool>();
    int end = 4;

    for (int i = 0; i < parsedText.Length; i++)
    {
        string subString = parsedText.Substring(i, 4);
        if (IsHot(subString, knownChar))
        {
            end += i;
            break;
        }

        knownChar.Clear();
    }

    Console.WriteLine(end);
}

